	* 
FROM
	alert_history s 
WHERE
	s.`id_alert` = #{id_alert} 
	AND s.`id_employee` = #{id_employee} 
	LIMIT 1;

SELECT ( SELECT COUNT( e.id_error_level ) AS lowPriority FROM alert a LEFT JOIN device d ON d.id = a.id_device LEFT JOIN error e ON a.id_error = e.id WHERE e.id_error_level = 13 AND a.`status` = 1 AND e.`status` = 1
AND d.id_site IN (
#{item.id}
)
GROUP BY e.id_error_level ) AS lowPriority,
( SELECT COUNT( e.id_error_level ) AS highPriority FROM alert a LEFT JOIN error e ON a.id_error = e.id LEFT JOIN device d ON d.id = a.id_device WHERE e.id_error_level = 11 AND a.`status` = 1 AND e.`status` = 1
AND d.id_site IN (
#{item.id}
)
GROUP BY e.id_error_level ) AS highPriority,
( SELECT COUNT(*) AS totalError FROM alert a LEFT JOIN error e ON a.id_error = e.id LEFT JOIN device d ON d.id = a.id_device WHERE a.`status` = 1 AND e.`status` = 1
AND d.id_site IN (
#{item.id}
)
) AS totalError

SELECT s.id, SHA1(s.id) AS hash_id, s.`name` AS site_name, s.dc_capacity, s.ac_capacity, SUM(if(d.id_device_type = 1, 1, 0)) AS totalInverter, SUM(if(d.id_device_type = 3, 1, 0)) AS totalMeter, IFNULL(SUM(if(d.id_device_type = 3, d.last_value, 0)), SUM(if(d.id_device_type = 1, d.last_value, 0))) AS power, l.last_updated, IFNULL(g.alertMeter, 0) AS alertMeter, IFNULL(g.alertInverter, 0) AS alertInverter, IFNULL(g.totalAlert, 0) AS totalAlert, al.start_date FROM site s LEFT JOIN device d ON d.id_site = s.id LEFT JOIN site_employee_map sem ON sem.id_site = s.id
LEFT JOIN ( SELECT s.id, a.start_date FROM alert a LEFT JOIN device d ON d.id = a.id_device LEFT JOIN site s ON s.id = d.id_site LEFT JOIN site_employee_map sem ON sem.id_site = s.id AND sem.id_employee = #{id_employee}
WHERE a.`status` = 1 AND d.`status` = 1 AND s.`status` = 1 GROUP BY d.id_site ORDER BY a.start_date DESC )al ON al.id = s.id
LEFT JOIN ( SELECT s.id, DATE_FORMAT(CONVERT_TZ( d.last_updated, '+00:00', tz.`offset` ), '%m/%d/%Y %H:%i:%s %p') AS last_updated FROM device d LEFT JOIN site s ON s.id = d.id_site LEFT JOIN time_zone tz ON tz.id = s.id_time_zone LEFT JOIN site_employee_map sem ON sem.id_site = s.id AND sem.id_employee = #{id_employee} WHERE d.id_device_type = 5 AND s.`status` = 1 AND s.is_delete = 0 AND d.`status` = 1 AND d.`is_delete` = 0 GROUP BY s.id )l ON l.id = s.id
LEFT JOIN ( SELECT s.id, SUM(if(d.id_device_type = 3, 1, 0)) AS alertMeter, SUM(if(d.id_device_type = 1, 1, 0)) AS alertInverter, COUNT(a.id) AS totalAlert FROM alert a LEFT JOIN error e ON e.id = a.id_error LEFT JOIN device d ON d.id = a.id_device LEFT JOIN site s ON s.id = d.id_site LEFT JOIN site_employee_map sem ON sem.id_site = s.id AND sem.id_employee = #{id_employee} WHERE a.`status` = 1 AND a.is_delete = 0 AND e.`status` = 1 AND e.`is_delete` = 0 GROUP BY s.id )g ON g.id = s.id WHERE s.`status` = 1 AND s.is_delete = 0 AND d.`status` = 1 AND d.is_delete = 0 AND totalAlert > 0 AND sem.id_employee = #{id_employee}
AND s.`name` LIKE CONCAT("%",#{site_name}, "%")
GROUP BY s.id
ORDER BY s.`name` ${order_by}
ORDER BY g.`totalAlert` ${order_by}
ORDER BY g.`alertInverter` ${order_by}
ORDER BY g.`alertMeter` ${order_by}
ORDER BY s.dc_capacity ${order_by}
ORDER BY `power` ${order_by}
ORDER BY d.`last_updated` ${order_by}
ORDER BY al.start_date DESC
LIMIT #{limit} OFFSET #{offset};

SELECT l.id, l.asset, el.name AS priority_name, l.capacity, l.`status`, l.note, l.alert_acknowledged, l.disable_notification, l.resolved, IF(l.`status` = 1, 'Open', 'Close') AS status_name, el.`name` AS `level`, e.message, e.error_code, d.devicename, s.`name` AS site_name, s.id AS id_site, d.id as id_device, i.icon AS icon_alert, el.id AS id_error_level, el.level, DATE_FORMAT( CONVERT_TZ( l.start_date, t.`offset`, #{offset_timezone} ), #{format_sql_long} ) AS start, DATE_FORMAT( CONVERT_TZ( l.start_date,'+00:00', t.`offset`) , '%m/%d/%Y %H:%i %p') AS start_date, DATE_FORMAT(CONVERT_TZ( l.end_date,'+00:00', t.`offset`), '%m/%d/%Y %H:%i %p') AS end_date, DATE_FORMAT(CONVERT_TZ( d.last_updated,'+00:00', t.`offset`), '%m/%d/%Y %H:%i %p') AS last_updated,
CONCAT_WS('', IF( FLOOR((TIMESTAMPDIFF(MINUTE,CONVERT_TZ( l.start_date,'+00:00', t.`offset`),#{current_time}) / (60*24))) < 0 , 0, FLOOR((TIMESTAMPDIFF(MINUTE,CONVERT_TZ( l.start_date,'+00:00', t.`offset`), #{current_time}) / (60*24)))), 'd ', IF(MOD(FLOOR((TIMESTAMPDIFF(MINUTE,CONVERT_TZ( l.start_date,'+00:00', t.`offset`),#{current_time}) / 60)), 24) < 0, 0, MOD(FLOOR((TIMESTAMPDIFF(MINUTE,CONVERT_TZ( l.start_date,'+00:00', t.`offset`),#{current_time}) / 60)), 24)), 'h ', IF( MOD((TIMESTAMPDIFF(MINUTE,CONVERT_TZ( l.start_date,'+00:00', t.`offset`), #{current_time})), 60) < 0, 0, MOD((TIMESTAMPDIFF(MINUTE,CONVERT_TZ( l.start_date,'+00:00', t.`offset`), #{current_time})), 60)), 'm' ) AS duration, IFNULL( FLOOR((TIMESTAMPDIFF(MINUTE,CONVERT_TZ( l.start_date,'+00:00', t.`offset`), #{current_time}))) , 0) AS open_period,
CASE WHEN CONVERT_TZ( l.start_date, '+00:00', t.`offset` ) < DATE_ADD( DATE_FORMAT( CONVERT_TZ( NOW(), '+00:00', t.`offset` ) , '%Y-%m-%d %H:%i'), INTERVAL -1 DAY ) THEN TIMESTAMPDIFF( DAY, CONVERT_TZ( l.start_date, '+00:00', t.`offset` ), DATE_FORMAT( CONVERT_TZ( NOW(), '+00:00', t.`offset` ) , '%Y-%m-%d %H:%i') ) WHEN CONVERT_TZ( l.start_date, '+00:00', t.`offset` ) < DATE_ADD( DATE_FORMAT( CONVERT_TZ( NOW(), '+00:00', t.`offset` ) , '%Y-%m-%d %H:%i'), INTERVAL -1 HOUR ) THEN TIMESTAMPDIFF( HOUR, CONVERT_TZ( l.start_date, '+00:00', t.`offset` ), DATE_FORMAT( CONVERT_TZ( NOW(), '+00:00', t.`offset` ) , '%Y-%m-%d %H:%i') ) WHEN CONVERT_TZ( l.start_date, '+00:00', t.`offset` ) < DATE_ADD( DATE_FORMAT( CONVERT_TZ( NOW(), '+00:00', t.`offset` ) , '%Y-%m-%d %H:%i'), INTERVAL -1 MINUTE ) THEN TIMESTAMPDIFF( MINUTE, CONVERT_TZ( l.start_date, '+00:00', t.`offset` ), DATE_FORMAT( CONVERT_TZ( NOW(), '+00:00', t.`offset` ) , '%Y-%m-%d %H:%i') ) ELSE 0 END AS times_ago,
CASE WHEN CONVERT_TZ( l.start_date, '+00:00', t.`offset` ) < DATE_ADD( DATE_FORMAT( CONVERT_TZ( NOW(), '+00:00', t.`offset` ) , '%Y-%m-%d %H:%i'), INTERVAL - 1 DAY ) THEN 'day' WHEN CONVERT_TZ( l.start_date, '+00:00', t.`offset` ) < DATE_ADD( DATE_FORMAT( CONVERT_TZ( NOW(), '+00:00', t.`offset` ) , '%Y-%m-%d %H:%i'), INTERVAL - 1 HOUR ) THEN 'hour' WHEN CONVERT_TZ( l.start_date, '+00:00', t.`offset` ) < DATE_ADD( DATE_FORMAT( CONVERT_TZ( NOW(), '+00:00', t.`offset` ) , '%Y-%m-%d %H:%i'), INTERVAL - 1 MINUTE ) THEN 'minute' ELSE 'now' END AS times_ago_unit
FROM `alert` l LEFT JOIN error e ON e.id = l.id_error LEFT JOIN error_level el ON el.id = e.id_error_level LEFT JOIN icon i ON i.id = el.id_icon LEFT JOIN device d ON d.id = l.id_device
LEFT JOIN site s ON s.id = d.id_site LEFT JOIN time_zone t ON t.id = s.id_time_zone WHERE e.`status` = 1 AND e.is_delete = 0 AND l.is_delete = 0 AND l.`status` = 1 AND s.id = #{id}
AND el.id IN (
#{item.id}
)
AND s.`name` LIKE CONCAT("%",#{site_name}, "%")
GROUP BY l.id
ORDER BY l.`id` ${order_by}
ORDER BY s.`name` ${order_by}
ORDER BY el.`level` ${order_by}
ORDER BY e.`message` ${order_by}
ORDER BY d.`devicename` ${order_by}
ORDER BY l.`start_date` ${order_by}
ORDER BY l.`end_date` ${order_by}
ORDER BY `open_period` ${order_by}
ORDER BY l.start_date DESC
LIMIT #{limit} OFFSET #{offset};
