mapper.select:
SELECT id, name, id_generic_parameter_type FROM generic_parameters
WHERE name LIKE CONCAT("%",#{keyword}, "%")
ORDER BY
${sort_column} ${order_by}
id DESC
LIMIT ${limit} OFFSET ${offset};

mapper.insert:
	INSERT INTO generic_parameters (
		name,
		id_generic_parameter_type
	) VALUES (
		#{name},
		#{id_generic_parameter_type}
	);

        SELECT 
        	LAST_INSERT_ID() as id

mapper.update:
UPDATE generic_parameters
SET
	name = #{name},
	id_generic_parameter_type = #{id_generic_parameter_type}
WHERE
	id = #{id}
