mapper.select:
SELECT
ROUND((u.irradiance * 0.1), 2) AS irradiance, ROUND((u.tcell * 0.1 - 25), 2) AS tcell,
u.*, CONCAT_WS(" ", DATE_FORMAT( CONVERT_TZ( u.time, '+00:00', t.`offset` ), '%m/%d/%Y %H:%i:%s %p' ), t.abbreviation_std) AS last_communication FROM ${datatablename} u LEFT JOIN device d ON u.id_device = d.id LEFT JOIN site s ON s.id = d.id_site LEFT JOIN time_zone t ON t.id = s.id_time_zone
WHERE SHA1(u.id_device) = #{hash_id}
ORDER BY `time` DESC LIMIT 1;
